import {Component} from 'react'
import {connect} from 'redux-zero/react';
import fn from '../../states/fn';
import Router from 'next/router'
import style from './style.css'

import Default from '../../layouts/default'

const mapToProps = ({ s , d}) => ({ s, d });

class Login extends Component {
    constructor(props) {
        super(props)
        this.handleEmailInput = this.handleEmailInput.bind(this)
        this.handlePasswordInput = this.handlePasswordInput.bind(this)
        this.state = {
            email: '',
            password: ''
        }
    }

    handleEmailInput = e => {
        if(e.keyCode === 13) {
            e.preventDefault()
            e.stopPropagation()
            this.setState({
                email: e.target.value
            })
            this.props.checkAuthEmail(e.target.value)
        } else {
            this.setState({
                email: e.target.value
            })
        }
    }

    handlePasswordInput = e => {
        if(e.keyCode === 13) {
            e.preventDefault()
            e.stopPropagation()
            this.setState({
                password: e.target.value
            })
            this.props.checkAuthEmailPassword(this.state.email, e.target.value)
            e.target.focus()
        } else {
            this.setState({
                password: e.target.value
            })
        }
    }

    render() {
        return (
            <div class={style.i}>
                <section>
                    <div class={style.loginSec}>
                        <h1>Sign in to FamilyLand, Inc.</h1>
                        <div class={this.state.signUp ? style.none : ''}>
                            <form name="login">
                                <input autofocus type="email" name="email" placeholder="Your Company E-mail" autocomplete="on" onKeyDown={this.handleEmailInput} value={this.state.email} />

                                {Object.keys(this.props.s.set).length === 0 ? null : this.props.s.set.email.valid ? <input autocomplete="on" autoFocus type="password" name="password" placeholder="Password" onKeyDown={this.handlePasswordInput} disabled={Object.keys(this.props.s.set).length === 0 ? false : this.props.s.set.password.loading}/> : null}
                                <p class={style.errorMassage}>{Object.keys(this.props.s.set).length === 0 ? '' : this.props.s.set.msg}</p>
                            </form>
                            <p style={{fontSize: 11}}>Don't have an account? <span class={style.signup}>Contact to Admin</span></p>
                        </div>
                    </div>
                </section>
            </div>
        )
    }
}


export default connect(mapToProps, fn)(Login)
